import { get } from '@/utils/request';
export default {
  data() {
    return {
      currentPage: 1,
      pagesize: 15,
      dialogVisible: false,
      dialogVisible1: false,
      list: []
    };
  },

  mounted() {
    get('http://localhost:8080/allOrder').then(res => {
      const orderDetailMap = new Map();
      const orderList = res.data.data;
      get('http://localhost:8080/allOrderDetail').then(res2 => {
        const orderDetailList = res2.data.data;
        orderDetailList.forEach(item => {
          orderDetailMap.set(item.orderId, item);
        });
        orderList.forEach(item => {
          const detail = orderDetailMap.get(item.orderId);
          item.userId = detail.userId;
          item.totalPrice = detail.count * detail.price; // 计算总价

          item.orderTime = detail.orderTime;
        });
        this.list = orderList;
      });
    });
  },

  methods: {
    handleSizeChange(size) {
      this.pagesize = size;
    },

    handleCurrentChange(currentPage) {
      this.currentPage = currentPage;
    }

  }
};
