import {
  createRouter,
  createWebHistory
} from 'vue-router'

//引入布局组件
import Layout from '../views/Layout/index.vue'

const routes = [{
  path: '/',
  name: 'docHome',
  meta: {
    name: '医生登录'
  },
  component: () => import('../views/Login/Login_doctor.vue'),
  children: []
},
  {
    path: '/admin/login',
    name: 'adminLogin',
    hidden: true,
    meta: {
      name: '管理员登录'
    },
    component: () => import('../views/Login/Login_admin.vue')
  },
  {
    path: '/reg',
    name: 'Reg',
    hidden: true,
    meta: {
      name: '注册'
    },
    //按需加载
    component: () => import('../views/Login/Reg.vue')
  },
  {
    path: '/:catchAll(.*)',
    name: '404',
    hidden: true,
    meta: {
      name: '404页面'
    },
    //按需加载
    component: () => import('../views/404/404.vue')
  },
  {
    path: '/admin/doctor',
    name: 'adminDoctor',
    hidden: true,
    meta: {
      name: '医生管理'
    },
    component: () => import('../views/Admin/doctor.vue')
  },
  {
    path: '/admin/user',
    name: 'adminUser',
    hidden: true,
    meta: {
      name: '用户管理'
    },
    component: () => import('../views/Admin/user.vue')
  },
  {
    path: '/equipment',
    name: 'equipment',
    //重定向
    meta: {
      name: '药品管理',
      icon: 'School'
    },
    //控制台
    component: Layout,
    children: [{
      path: '/equipment',
      name: 'equipment',
      meta: {
        name: '器械管理'
      },
      component: () => import('../views/Admin/medicine.vue')
    }]
  },
  {
    path: '/type',
    name: 'type',
    //重定向
    meta: {
      name: '药品类型管理',
      icon: 'Notebook'
    },
    //控制台
    component: Layout,
    children: [{
      path: '/type',
      name: 'type',
      meta: {
        name: '器械类型管理'
      },
      component: () => import('../views/Admin/medicineType.vue')
    }]
  },
  {
    path: '/collect',
    name: 'collect',
    //重定向
    meta: {
      name: '订单管理',
      icon: 'Operation'
    },
    //控制台
    component: Layout,
    children: [{
      path: '/collect',
      name: 'collect',
      meta: {
        name: '收藏'
      },
      component: () => import('../views/Admin/order.vue')
    }]
  },

  {
    path: '/userIndex',
    name: 'userIndex',
    hidden: true,
    meta: {
      name: '用户端首页',
      require: true
    },
    component: () => import('../views/User/userIndex.vue')
  },
  {
    path: '/personal',
    name: 'personal',
    hidden: true,
    meta: {
      name: '个人中心',
      require: true
    },
    component: () => import('../views/User/personal.vue')
  },
  {
    path: '/myCollect',
    name: 'myCollect',
    hidden: true,
    meta: {
      name: '我的收藏',
      require: true
    },
    component: () => import('../views/User/shoppingCart.vue')
  },
  {
    path: '/detail',
    name: 'detail',
    hidden: true,
    meta: {
      name: '详情',
      require: true
    },
    component: () => import('../views/User/detail.vue')
  },
  {
    path: '/test',
    name: 'test',
    hidden: true,
    meta: {
      name: 'test',
      require: true
    },
    component: () => import('../views/User/test.vue')
  },
  {
    path: '/news1',
    name: 'news1',
    hidden: true,
    meta: {
      name: 'news1',
      require: true
    },
    component: () => import('../views/User/News/news1.vue')
  },
  {
    path: '/news2',
    name: 'news2',
    hidden: true,
    meta: {
      name: 'news2',
      require: true
    },
    component: () => import('../views/User/News/news2.vue')
  },
  {
    path: '/news3',
    name: 'news3',
    hidden: true,
    meta: {
      name: 'news3',
      require: true
    },
    component: () => import('../views/User/News/news3.vue')
  },
  {
    path: '/medicine',
    name: 'medicine',
    hidden: true,
    meta: {
      name: 'medicine',
      require: true
    },
    component: () => import('../views/User/medicine.vue')
  },
  {
    path: '/order',
    name: 'order',
    hidden: true,
    meta: {
      name: 'order',
      require: true
    },
    component: () => import('../views/User/order.vue')
  }

]

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes
})

router.beforeEach((to, from, next) => {
  const username = sessionStorage.getItem('username')
  if (to.meta.require && !username) {
    // 如果当前用户未登录且要跳转的页面需要登录，则跳转到登录页面
    next({
      name: 'Login'
    })
  } else {
    next()
  }
})


export default router
