import { get } from '@/utils/request';
import { ElMessage } from 'element-plus';
export default {
  data() {
    return {
      currentPage: 1,
      //默认页码为1
      pagesize: 15,
      //默认一页显示 条
      dialogVisible: false,
      dialogVisible1: false,
      info2: {},
      //修改数据
      info1: {},
      //新增数据
      list: [],
      adg: []
    };
  },

  //初始化工作
  mounted() {
    get('http://localhost:8080/equipmentList').then(res => {
      console.log(res);
      this.list = res.data;
    });
  },

  methods: {
    del(data) {
      console.log(data.row.id);
      get('http://localhost:8080/deleteEquipment', {
        id: data.row.id
      }).then(() => {
        ElMessage({
          message: '删除成功！',
          type: 'success'
        });
      });
      window.location.reload(); //刷新
    },

    handleSizeChange: function (size) {
      //一页显示多少条
      this.pagesize = size;
    },
    handleCurrentChange: function (currentPage) {
      //页码更改方法
      this.currentPage = currentPage;
    }
  }
};
