import {
  createRouter,
  createWebHistory
} from 'vue-router'

//引入布局组件
import Layout from '../views/Layout/index.vue'

const routes = [{
  path: '/',
  name: 'docHome',
  meta: {
    name: '医生登录'
  },
  component: () => import('../views/Login/Login_doctor.vue'),
  children: []
},
  {
    path: '/admin/login',
    name: 'adminLogin',
    hidden: true,
    meta: {
      name: '管理员登录'
    },
    component: () => import('../views/Login/Login_admin.vue')
  },
  {
    path: '/reg',
    name: 'Reg',
    hidden: true,
    meta: {
      name: '注册'
    },
    //按需加载
    component: () => import('../views/Login/Reg.vue')
  },
  {
    path: '/:catchAll(.*)',
    name: '404',
    hidden: true,
    meta: {
      name: '404页面'
    },
    //按需加载
    component: () => import('../views/404/404.vue')
  },
  {
    path: '/admin/Doctor',
    name: 'adminDoctor',
    meta: {
      name: '医生管理'
    },
    component: Layout, // 使用布局组件
    children: [
      {
        path: '',
        component: () => import('../views/Admin/doctor.vue')
      }
    ]
  },
  {
    path: '/admin/user',
    name: 'adminUser',
    meta: {
      name: '用户管理'
    },
    component: Layout, // 使用布局组件
    children: [
      {
        path: '',
        component: () => import('../views/Admin/user.vue')
      }
    ]
  },
  {
    path: '/admin/admin',
    name: 'adminAdmin',
    component: Layout, // 使用布局组件
    meta: {
      name: '管理员管理'
    },
    children: [
      {
        path: '',
        component: () => import('../views/Admin/admin.vue')
      }
    ]
  },
  {
    path: '/admin/personal',
    name: 'adminPersonal',
    meta: {
      name: '个人信息管理'
    },
    component: Layout, // 使用布局组件
    children: [
      {
        path: '',
        component: () => import('../views/Admin/personal.vue')
      }
    ]
  },
  {
    path: '/admin/notice',
    name: 'adminNotice',
    meta: {
      name: '公告管理'
    },
    component: Layout, // 使用布局组件
    children: [
      {
        path: '',
        component: () => import('../views/Admin/notice.vue')
      }
    ]
  },
  {
    path: '/admin/store',
    name: 'adminStore',
    meta: {
      name: '商城管理'
    },
    component: Layout, // 使用布局组件
    children: [
      {
        path: '',
        component: () => import('../views/Admin/store.vue')
      }
    ]
  },
  {
    path: '/Doctor/patient',
    name: 'doctorPatient',
    hidden: true,
    meta: {
      name: '医生病人管理'
    },
    component: () => import('@/views/Doctor/patient.vue')
  },
  {
    path: '/Doctor/personal',
    name: 'doctorPersonal',
    hidden: true,
    meta: {
      name: '医生个人信息管理'
    },
    component: () => import('@/views/Doctor/personal.vue')
  },
  {
    path: '/Doctor/message',
    name: 'doctorMessage',
    hidden: true,
    meta: {
      name: '医生聊天'
    },
    component: () => import('@/views/Doctor/messageList.vue')
  },
  {
    path: '/Doctor/emr',
    name: 'doctorEmr',
    hidden: true,
    meta: {
      name: '医生写病历'
    },
    component: () => import('@/views/Doctor/emr.vue')
  },
  {
    path: '/Doctor/prescription',
    name: 'doctorPrescription',
    hidden: true,
    meta: {
      name: '医生写处方'
    },
    component: () => import('@/views/Doctor/prescription.vue')
  },
  {
    path: '/Doctor/userTest',
    name: 'doctor/userTest',
    hidden: true,
    meta: {
      name: '用户模拟'
    },
    component: () => import('@/views/Doctor/userTest.vue')
  },
  {
    path: '/userIndex',
    name: 'userIndex',
    hidden: true,
    meta: {
      name: '用户端首页',
      require: true
    },
    component: () => import('../views/User/userIndex.vue')
  },
  {
    path: '/personal',
    name: 'personal',
    hidden: true,
    meta: {
      name: '个人中心',
      require: true
    },
    component: () => import('../views/User/personal.vue')
  },
  {
    path: '/myCollect',
    name: 'myCollect',
    hidden: true,
    meta: {
      name: '我的收藏',
      require: true
    },
    component: () => import('../views/User/shoppingCart.vue')
  },
  {
    path: '/detail',
    name: 'detail',
    hidden: true,
    meta: {
      name: '详情',
      require: true
    },
    component: () => import('../views/User/detail.vue')
  },
  {
    path: '/test',
    name: 'test',
    hidden: true,
    meta: {
      name: 'test',
      require: true
    },
    component: () => import('../views/User/test.vue')
  },
  {
    path: '/news1',
    name: 'news1',
    hidden: true,
    meta: {
      name: 'news1',
      require: true
    },
    component: () => import('../views/User/News/news1.vue')
  },
  {
    path: '/news2',
    name: 'news2',
    hidden: true,
    meta: {
      name: 'news2',
      require: true
    },
    component: () => import('../views/User/News/news2.vue')
  },
  {
    path: '/news3',
    name: 'news3',
    hidden: true,
    meta: {
      name: 'news3',
      require: true
    },
    component: () => import('../views/User/News/news3.vue')
  },
  {
    path: '/medicine',
    name: 'medicine',
    hidden: true,
    meta: {
      name: 'medicine',
      require: true
    },
    component: () => import('../views/User/medicine.vue')
  },
  {
    path: '/order',
    name: 'order',
    hidden: true,
    meta: {
      name: 'order',
      require: true
    },
    component: () => import('../views/User/order.vue')
  }

]

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes
})

router.beforeEach((to, from, next) => {
  const username = sessionStorage.getItem('username')
  if (to.meta.require && !username) {
    // 如果当前用户未登录且要跳转的页面需要登录，则跳转到登录页面
    next({
      name: 'Login'
    })
  } else {
    next()
  }
})


export default router
